public class ArrayList<E> implements List<E> {
    //初始化大小
    public static final int DEFAULT_CAPACITY = 10;
    private static final Object[] DEFAULTCAPACITY_EMPTY_ELEMENTDATA = {};

    //元素数组
    private Object[] elementData;
    //元素个数
    private int size = 0;

    /**
     * 线性表大小
     *
